def main():
    new_vacancies = []
    for all in vacancies['list']:
        vacancy = dict()
        print(all['title'])
        vacancy['title'] = all['title']
        if all['location']:
            vacancy['location'] = all['location']['title']
        vacancy['company'] = all['company']['title']
        if all['company']['logo']:
            vacancy['company_logo'] = all['company']['logo']['src']
        vacancy['specialty'] = all['divisions'][0]['title']
        vacancy['skills'] = [skill['title'] for skill in all['skills']]
        if all['salary']['from']:
            vacancy['salary_min'] = all['salary']['from']
        if all['salary']['to']:
            vacancy['salary_max'] = all['salary']['to']
        url = 'https://career.habr.com'+all['href']
        r = requests.get(url)
        soup = BeautifulSoup(r.text)
        p = [a.text for a in soup.find_all(class_='sanitize-ugc')]
        vacancy['description'] = ' '.join(p)
        new_vacancies.append(vacancy)

    with open('vacancies.json', 'w') as f:
        json.dump(new_vacancies, f)



if __name__ == '__main__':
    main()
